from typing import List

class Solution:

    def __init__(self):
        self.cache: list[str] = [None] * 1001
        self.input_words: list[str] = []
        self.layer: list[list[str]] = [[] for i in range(100)]
        self.layer_size = 0
        self.longest_chain = 1

    def is_predecessor(self, word1:str, word2:str):
        if len(word1) + 1 != len(word2):
            return False
        idx1 = 0
        idx2 = 0
        first_diff = True
        while idx1 < len(word1) and idx2 < len(word2):
            # print(word1, word2, idx1, idx2)
            if word1[idx1] == word2[idx2]:
                idx1 += 1
                idx2 += 1
            else:
                if first_diff:
                    first_diff = False
                    idx2 += 1
                else:
                    return False
        return True

    # def get_lsc(self, words: list[str], words_len) -> list[str]:
    #     if self.cache[words_len] is not None:
    #         return self.cache[words_len]
    #
    #     if words_len == 0:
    #         result = []
