    #     else:
    #         result_n_1 = self.get_lsc(words, words_len - 1)
    #         if self.is_predecessor(result_n_1[-1], words[words_len - 1]):
    #             result = result_n_1.copy()
    #             result.append(words[words_len - 1])
    #         else:
    #             result = result_n_1.copy()
    #     return result

    def get_lsc(self, layer_idx: int) -> list[list[str]]:
        if layer_idx == 0:
            return []

        lsc: list[list[str]] = []
        # print(layer_idx)
        words = self.layer[layer_idx]
        word_set = set(words)
        last_lsc = self.get_lsc(layer_idx - 1)

        for word in words:
            for lsc_words in last_lsc:
                lsc_last_word = lsc_words[-1]
                if self.is_predecessor(lsc_last_word, word):
                    to_add = lsc_words.copy()
                    to_add.append(word)
                    lsc.append(to_add)
                    if len(to_add) > self.longest_chain:
                        self.longest_chain = len(to_add)
                    if word in word_set:
                        word_set.remove(word)
                    break

        for word in word_set:
            lsc.append([word])
        # print(f"lsc={lsc}")
        return lsc

    def set_layer(self, words: list[str]):
        for word in words:
            self.layer[len(word)].append(word)
            if len(word) > self.layer_size:
                self.layer_size = len(word)
        self.layer_size += 1

    def longestStrChain(self, words: List[str]) -> int:
        self.set_layer(words)
        # print(self.layer)
        # print(self.layer_size)
        self.get_lsc(self.layer_size)
        # print(self.longest_chain)
        return self.longest_chain

