from typing import List
from functools import cache
import math

class Solution:
    def maximumScore(self, nums: List[int], multipliers: List[int]) -> int:
        dp = [[-math.inf] * (len(multipliers) + 1 - i) for i in range(len(multipliers) + 1)]
        dp[0][0] = 0
        result = -math.inf
        for i in range(len(dp)):
            for j in range(len(dp)):
                # if i + j > len(multipliers):
                #     break
                if i == 0 and j > 0:
                    dp[i][j] = dp[i][j - 1] + nums[-j] * multipliers[j + i - 1]
                elif j == 0 and i > 0:
                    dp[i][j] = dp[i - 1][j] + nums[i - 1] * multipliers[j + i - 1]
                elif i > 0 and j > 0:
                    dp[i][j] = max(dp[i - 1][j] + nums[i - 1] * multipliers[j + i - 1]
                                   , dp[i][j - 1] + nums[-j] * multipliers[j + i - 1])
                if i + j == len(multipliers):
                    result = max(result, dp[i][j])
                    break
        return result

