class Solution:
    def longestBeautifulSubstring(self, word: str) -> int:
        vow_idx = {ch: i for i, ch in enumerate("aeiou")}
        cur_pos = -1
        sub_len = 0
        ans = 0
        for ch in word:
            if vow_idx[ch] == cur_pos:
                sub_len += 1
            elif ch == "a":
                cur_pos = 0
                sub_len = 1
            elif vow_idx[ch] == cur_pos + 1:
                sub_len += 1
                cur_pos += 1
            else:
                cur_pos = -1
                sub_len = 0
            if ch == "u" and cur_pos == 4:
                ans = max(ans, sub_len)
        return ans
    
# data = "iaeiouuuuuuiu"
data = ""
r = Solution().longestBeautifulSubstring(data)
print(r)
