from collections import Counter

class Solution:
    def longestPalindrome(self, s: str) -> int:
        cnt_dict = Counter(s)
        has_odd = False
        cnt_even = 0
        print(cnt_dict)
        for key, val in cnt_dict.items():
            if val & 1 == 0:
                cnt_even += val
            else:
                cnt_even += (val - 1)
                has_odd = True

        return cnt_even + (1 if has_odd else 0)

data = ""
r = Solution().longestPalindrome(data)
print(r)